const packetIdCacheGloabalKey = 'packetIdCache'
const expirySeconds = 300;
var idCache = global.get(packetIdCacheGloabalKey)

if (idCache === undefined) {
    idCache = {}
}

// clean dead keys
for (var key in idCache) {
    if (idCache[key] <= new Date().getTime()) {
        delete idCache[key];
    }
};

var id = msg.payload.packet.id

if (idCache[id]) {
    // message is a duplicate, send to the duplicate output and exit
    return [null, msg];
}

idCache[id] = new Date().getTime() + (expirySeconds * 1000)

global.set(packetIdCacheGloabalKey, idCache);

return [msg, null];

// Initialize an array to store non-null and non-NaN "lat" values
let validLatValues = [];

// Check if "payload" exists and is an array
if (msg.payload && Array.isArray(msg.payload)) {
    // Filter out objects where "lat" value is neither null nor NaN
    validLatValues = msg.payload.filter(function(item) {
        return item.lat !== null && !isNaN(item.lat);
    });

    // If valid "lat" values are found, set payload to the original payload
    if (validLatValues.length > 0) {
        msg.payload = msg.payload;
    } else {
        // If no valid "lat" values found, set payload to null to discard the message
        msg.payload = null;
    }
} else {
    // If payload does not exist or is not an array, set it to null to discard the message
    msg.payload = null;
}

// Output valid "lat" values to the debug console
