// JavaScript function to calculate session ID
function generateSessionID(userID) {
    // Convert hexadecimal user_id to integer
    var user_id_int = parseInt(userID.substring(1), 16);

    // Generate a random session ID with the last three bytes matching the user ID
    var session_id = Math.floor(Math.random() * (Math.pow(2, 24) - 1)) | user_id_int;

    return session_id.toString();
}

// Check if the incoming message has the "fromShortName" property
if (msg.payload && Array.isArray(msg.payload) && msg.payload.length > 0 && msg.payload[1] && msg.payload[1].fromShortName) {
    var fromShortName = msg.payload[1].fromShortName;

    // Use fromShortName as user
    var user = fromShortName;

    // Calculate the session ID for the user
    var sid = generateSessionID(user);

    // Extract other values from the payload
    var lat = msg.payload[0].lat;
    var lon = msg.payload[0].lon;
    var alt = msg.payload[0].alt;
    var tm = msg.payload[0].tm;
    var sog = msg.payload[0].groundSpeed;

    // Check if alt is NOT within the specified range (-1000 to 15000)
    if (!(alt >= -1000 && alt <= 15000)) {
        // If alt is NOT within the range, set it to 0
        alt = 0;
    }

    // Prepare the data payload for the HTTP POST request
    var postData = `leolive=1&user=${user}&lat=${lat}&lon=${lon}&sid=${sid}&password=123&sog=${sog}&alt=${alt}&tm=${tm}`;

    // Set up the HTTP request parameters
    msg.method = "POST";
    msg.url = "https://skylines.aero/track.php";
    msg.headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Content-Length': postData.length
    };
    msg.payload = postData;

    return msg;
} else {
